
public class javaClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(Math.abs(-26.7));//absolute value
		System.out.println(Math.ceil(8.2));//ceiling, rounds to integer that is closest to infinity
		System.out.println(Math.floor(2.3));//floor, rounds to integer closest to 0
		System.out.println(Math.max(9.2, 5.2));//Maximum, gives maximum of whichever one is higher
		System.out.println(Math.min(2.1, 6.9));//Minimum, gives the minimum of whichever one is lower
		System.out.println(Math.sqrt(25));//Square root, takes the square of a number
		
	}
}
